mapper.select:
SELECT a.id, a.name, a.type, a.url, a.status, a.seq, a.remark, am.name AS aclModuleName FROM sys_acl a LEFT JOIN sys_acl_module am ON a.acl_module_id = am.id WHERE a.acl_module_id = #{sysAclDto.aclModuleId}
AND ( am.name LIKE CONCAT('%', #{sysAclDto.keyWord}, '%') OR a.name LIKE CONCAT('%', #{sysAclDto.keyWord}, '%') OR a.url REGEXP #{sysAclDto.keyWord} )
ORDER BY a.operate_time
